import './SignUp.scss';
import React, { useRef, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Link } from 'react-router-dom';

const SignUp = () => {
    let final1 = 0;
    let final2 = 0;
    let final3 = 0;

    let comNameRef = useRef();
    let nameRef = useRef();
    let idRef = useRef();
    let pwRef = useRef();

    const navigate = useNavigate();

    //id조건 확인 함수
    const check_id = () => {
        let id = document.getElementById('id').value;

        //특수문자 있으면 안됨
        const SC = ["!", "@", "#", "$", "%", "~","^","&","*","(",")","-","+","="];
        let check_SC = 0;


        //길이 확인
        if(id.length === 0){
            document.getElementById('check1').innerHTML  = ' ';
            final1 = 0;
        }
        else{
            // 1. 6자 미만 또는 12자 초과시 빨간색 오류메세지 출력
            if(id.length < 6 || 12 < id.length){
                document.getElementById('check1').innerHTML = "아이디는 6글자 이상, 12글자 이하로 입력해주세요";
                document.getElementById('check1').style.color = 'red';
                final1 = 0;
            }
            else{
                // 2. 6 ~ 12글자인데 특수문자 포함된 경우
                document.getElementById('check1').innerHTML = ' '
                for (var i = 0; i < SC.length; i++) {
                    if (id.indexOf(SC[i]) !== -1) {
                        check_SC = 1;
                    }
                }
                if (check_SC > 0) {
                    document.getElementById('check1').innerHTML = '특수문자를 포함할 수 없습니다.';
                    document.getElementById('check1').style.color = 'red';
                    final1 = 0;
                } else {
                    document.getElementById('check1').innerHTML = '';
                    final1 = 1;
                }
            }
        }
    }


    // 비밀번호 조건 확인 함수
    const check_pw = () => {

        let pw = document.getElementById('pw').value;
        const SC = ["!", "@", "#", "$", "%", "~"];
        let check_SC = 0;


        if (pw.length === 0) {
            document.getElementById('check2').innerHTML = '';
            final2 = 0;
        } else {
            if (pw.length < 8 || pw.length > 16) {
                document.getElementById('check2').innerHTML = '비밀번호는 8글자 이상, 16글자 이하로 입력해주세요.';
                document.getElementById('check2').style.color = 'red';
                final2 = 0;
            } else {
                document.getElementById('check2').innerHTML = ''
                for (var i = 0; i < SC.length; i++) {
                    if (pw.indexOf(SC[i]) !== -1) {
                        check_SC = 1;
                    }
                }
                if (check_SC === 0) {
                    document.getElementById('check2').innerHTML = '특수문자가 포함되어있지 않습니다.';
                    document.getElementById('check2').style.color = 'red';
                    final2 = 0;
                } else {
                    document.getElementById('check2').innerHTML = '';
                    final2 = 1;
                }
            }


        }

    }

    const check_same = () => {
        let pw = document.getElementById('pw2').value;

        if (pw.length === 0) {
            document.getElementById('check2').innerHTML = '';
            final3 = 0;
        } else {
            if (document.getElementById('pw').value !== '' && pw !== '') {
                if (document.getElementById('pw').value === pw) {
                    document.getElementById('check2').innerHTML = '비밀번호가 일치합니다.';
                    document.getElementById('check2').style.color = 'blue';
                    final3 = 1;
                }
                else {
                    document.getElementById('check2').innerHTML = '비밀번호가 일치하지 않습니다.';
                    document.getElementById('check2').style.color = 'red';
                    final3 = 0;
                }
            }
        }
    }

    const check_final = () => {
        //아이디 조건 성립, 비밀번호 확인까지 일치 시 제출버튼 활성화
        if (document.getElementById("name").value !== ' ' && final1 === 1) {
            if (final2 && final3) {
                document.getElementById("completion").disabled = false;
                document.getElementById("completion").style.background = '#5FCB71';
            }
        }
    }

    const handleSingup = () => {
        axios
            .post("http://www.stock-manage-api.p-e.kr/signup/admin", {
                comName: comNameRef.current.value,
                name: nameRef.current.value,
                id: idRef.current.value,
                password: pwRef.current.value,
            })
            .then((res) => {
                console.log(res);
                if (res.status === 200) {
                    navigate("/");
                } else {
                    alert(res.error);
                    comNameRef.current.value = "";
                    nameRef.current.value = "";
                    idRef.current.value = "";
                    pwRef.current.value = "";
                    navigate("/Signup");
                }
                
            })
            .catch((e) => {
                console.error(e);
            });
       
    } 

    return (
        <div>
            <h1 align="center"> Inventory - Manager</h1>
            <form className="registForm" onInput={check_final}>
                <div id="mainBox">
                    <h3>회원가입</h3>

                    <p className="comName">회사명</p>
                    <div className="textform">
                        <input id="comName" className="textin" type="text" placeholder="ex)삼성전자"/>
                    </div>
                    <p className="name">이름</p>
                    <div className="textform">
                        <input id="name" className="textin" type="text" placeholder="ex)홍길동"/>
                    </div>

                    <p className="name">아이디</p>
                    <div className="textform">
                        <input className="textin" type="text" id="id" placeholder="6자리 이상 12자리 이하 영문/숫자"
                            onInput={check_id}/><span id="check1"></span>
                    </div>

                    <p className="name">비밀번호</p>
                    <div className="textform">
                        <input className="textin" type="password" id="pw" placeholder="8자리 이상 16자리 이하 영문/숫자/특수문자"
                            onInput={check_pw}/><span id="check2"></span>
                    </div>
                    <p className="name">비밀번호 확인</p>
                    <div className="textform">
                        <input className="textin" type="password" id="pw2" placeholder="비밀번호를 다시 입력해주세요"
                            onInput={check_same}/><span id="check3"></span>
                    </div>

                    <input id="completion" type="submit" className="btn" value="가입" onClick={handleSingup} />
                </div>
            </form>
        </div>
    )
}

export default SignUp;
