const ShowTime = () => {
    let dt = new Date();
    let year = dt.getFullYear();
    let month = dt.getMonth()+1;
    let date = dt.getDate();
    let day = dt.getDay();
    let hour = dt.getHours();
    let min = dt.getMinutes();
    if (month < 10) {
        month = '0' + month;
    }
    
    let nowDate = year+'/'+month+'/'+date+'/'+day;
    let nowTime = hour+'시'+' '+min+'분';

    return (
        <div className = "nowTime">
            {nowDate}
            {nowTime}
        </div>
    )
}

export default ShowTime;
