import { useEffect } from 'react';
import './ShowCom.scss'

const ShowCom = ({user}) => {
    useEffect(() => {
        if(user){
            const {com, name, role} = user;
        }
    })
    
    return (
        <div className="comInfo">
            {com}<br/>
            {role} {name} 님
        </div>
    )
}

export default ShowCom;
