import './ListPage.scss';



const ListPage = () => {
    axios
        .post("http://www.stock-manage-api.p-e.kr/login", {
            id: idRef.current.value,
            password: pwRef.current.value,
        })
        .then((res) => {
            console.log("handleLogin =>", res);
            if (res.data.code === 200) {
                window.sessionStorage.setItem("id", idRef.current.value); // 세션스토리지에 key : id , value : idRef.current.value로 저장
                // sessionsStorage는 창 닫으면 사라짐, localStorage는 안사라짐
                navigate("/MainPage");
            } else {
                alert("아이디, 패스워드가 정확하지 않습니다.");
                idRef.current.value = "";
                pwRef.current.value = "";
                navigate("/");
            }
        })
        .catch((e) => {
            console.error(e);
        });
    return (
        <div className="ListPage">
            <div className="sortBy">
                <div>No.</div>
                <div>제조사</div>
                <div>품명</div>
                <div>규격</div>
                <div>단위</div>
                <div>수량</div>
                <div>단위</div>
            </div>
            <div className="list">

            </div>
        </div>
    )
}

export default ListPage;
