import './ListPage.scss';
import axios from 'axios';
import { useState } from 'react';
import { useNavigate } from "react-router-dom";
import React from 'react';
import StockItem from './StockItem'
import { useEffect } from 'react';


const ListPage = () => {
    const navigate = useNavigate();
    const [items, setItems] = useState();

    useEffect(() => {
        let completed = false; //초기에는 실행해야 되기때문에 false flag 변수
    
        //query를 리턴하는 함수를 result에 할당
        async function get() {
          const result = await axios.get(
            "http://www.stock-manage-api.p-e.kr/"
          );
          if (!completed) setData(result.data);
        }
        get();
        return () => {
          completed = true;
        };
        //query가 변할때 useEffect를 실행해야하는 시점이다
      }, [query]);

    useEffect(() => {
        let isMounted = true;

        axios.defaults.headers.common['Authorization'] = `Bearer ${sessionStorage.getItem('token')}`
        axios
            .get("http://www.stock-manage-api.p-e.kr/")
            .then((res) => {
                if (isMounted) {
                    console.log(res);
                    setItems(res.data.result);
                    console.log(items);
                }

            })
            .catch((e) => {
                console.error(e);
            });

        return () => {
            isMounted = false;
        }

    }, []);

    const InsertStock = () => {
        navigate("/StockInsert");
    }

    return (
        <div className="ListPage">
            <div className="sortBy">
                <div>No.</div>
                <div>제조사</div>
                <div>품명</div>
                <div>규격</div>
                <div>단위</div>
                <div>수량</div>
                <div>단위</div>
            </div>
            <div className="list">
                {items.map(item => (
                    <StockItem item={item}></StockItem>
                ))}
            </div>
            <div><button onClick={InsertStock}>재고추가</button></div>
        </div>
    )
}

export default ListPage;
